mapper.select:
select
id, isUsed, create_time, update_time, remarks, user_id, fans_user, status
from u_fans


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and user_id = #{userId}


        and fans_user = #{fansUser}


        and status = #{status}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from u_fans


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and user_id = #{userId}


        and fans_user = #{fansUser}


        and status = #{status}
