mapper.select:
select
id, isUsed, create_time, update_time, remarks, user_id, teacher_id, flowers
from u_flowers


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and user_id = #{userId}


        and teacher_id = #{teacherId}


        and flowers = #{flowers}


order by id desc

select
id, isUsed, create_time, update_time, remarks, user_id, teacher_id, flowers
from u_flowers


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and user_id = #{userId}


        and teacher_id = #{teacherId}


        and flowers = #{flowers}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from u_flowers


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and user_id = #{userId}


        and teacher_id = #{teacherId}


        and flowers = #{flowers}
