mapper.select:
select
id, isUsed, create_time, update_time, remarks, a_name, a_head, a_type, province, city, area, school
from c_authors


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and a_name = #{aName}


        and a_head = #{aHead}


        and a_type = #{aType}


        and province = #{province}


        and city = #{city}


        and area = #{area}


        and school = #{school}


order by id desc

select
id, isUsed, create_time, update_time, remarks, a_name, a_head, a_type, province, city, area, school
from c_authors


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and a_name = #{aName}


        and a_head = #{aHead}


        and a_type = #{aType}


        and province = #{province}


        and city = #{city}


        and area = #{area}


        and school = #{school}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from c_authors


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


