mapper.select:
select
id, isUsed, create_time, update_time, remarks, user_id, r_image, r_name, composition_id, r_type
from c_readlog


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and user_id = #{userId}


        and r_image = #{rImage}


        and r_name = #{rName}


        and composition_id = #{compositionId}


        and r_type = #{rType}


order by id desc

select
id, isUsed, create_time, update_time, remarks, user_id, r_image, r_name, composition_id, r_type
from c_readlog


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and user_id = #{userId}


        and r_image = #{rImage}


        and r_name = #{rName}


        and composition_id = #{compositionId}


        and r_type = #{rType}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from c_readlog


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and user_id = #{userId}


        and r_image = #{rImage}


        and r_name = #{rName}


        and composition_id = #{compositionId}


        and r_type = #{rType}
