mapper.select:
select
id, isUsed, create_time, update_time, remarks, m_titleid, m_content, m_image, m_try, m_voice, m_videos
from m_articles


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and m_titleid = #{mTitleid}


        and m_content = #{mContent}


        and m_image = #{mImage}


        and m_try = #{mTry}


        and m_voice = #{mVoice}


        and m_videos = #{mVideos}

select
id, isUsed, create_time, update_time, remarks, m_titleid, m_content, m_image, m_try, m_voice, m_videos
from m_articles


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and m_titleid = #{mTitleid}


        and m_content = #{mContent}


        and m_image = #{mImage}


        and m_try = #{mTry}


        and m_voice = #{mVoice}


        and m_videos = #{mVideos}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from m_articles


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


