mapper.select:
select
id, isUsed, create_time, update_time, remarks, magazines_id, columnname
from m_column


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and magazines_id = #{magazinesId}


        and columnname = #{columnname}

select
id, isUsed, create_time, update_time, remarks, magazines_id, columnname
from m_column


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and magazines_id = #{magazinesId}


        and columnname = #{columnname}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from m_column


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and magazines_id = #{magazinesId}


        and columnname = #{columnname}
