mapper.select:
select
id, isUsed, create_time, update_time, remarks, m_title, pid, m_author, m_school, m_try, m_image
from m_titles


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and m_title = #{mTitle}


        and pid = #{pid}


        and m_author = #{mAuthor}


        and m_school = #{mSchool}


        and m_try = #{mTry}


        and m_image = #{mImage}

