mapper.select:
select
id, isUsed, create_time, update_time, remarks, m_mag, m_image, m_type
from magazines


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and m_mag = #{mMag}


        and m_image = #{mImage}


        and m_type = #{mType}

select
id, isUsed, create_time, update_time, remarks, m_mag, m_image, m_type
from magazines


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and m_mag = #{mMag}


        and m_image = #{mImage}


        and m_type = #{mType}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from magazines


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and m_mag = #{mMag}


        and m_image = #{mImage}


        and m_type = #{mType}
