mapper.select:
select
id, isUsed, create_time, update_time, remarks, a_name, a_synopsis, a_status, a_type, a_award
from auth_task


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and a_name = #{aName}


        and a_synopsis = #{aSynopsis}


        and a_status = #{aStatus}


        and a_type = #{aType}


        and a_award = #{aAward}


order by id desc

select
id, isUsed, create_time, update_time, remarks, a_name, a_synopsis, a_status, a_type, a_award
from auth_task


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and a_name = #{aName}


        and a_synopsis = #{aSynopsis}


        and a_status = #{aStatus}


        and a_type = #{aType}


        and a_award = #{aAward}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from auth_task


        and id = #{id}


        and isUsed = #{isused}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and remarks = #{remarks}


        and a_name = #{aName}


        and a_synopsis = #{aSynopsis}


        and a_status = #{aStatus}


        and a_type = #{aType}


        and a_award = #{aAward}
